import React, {useState} from 'react';

import './AddUser.css'
import Card from '../UI/Card';
import Button from '../UI/Button';
import ErrorModal from '../UI/ErrorModal';

const AddUser = props =>{

    const [nameValue, setNameValue] = useState('');
    const [ageValue, setAgeValue] = useState('');

    const addNameHandler = event => {
        setNameValue(event.target.value);
    };
    const addAgeHandler = event => {
        setAgeValue(event.target.value);
    };
    

    const addUserHandler = event => {
        event.preventDefault();
        if (ageValue.trim().length === 0 || nameValue.trim().length === 0 || +ageValue<1){
            return     
        };
        props.onAddUsers(nameValue, ageValue);
        setNameValue('');
        setAgeValue('');

    };
    return (
        <div>
        <ErrorModal title="An error occured" message="something went wrong"/>
        <Card className='input'>
            <form onSubmit={addUserHandler}>
                <label htmlFor='username'>Username</label>
                <input id='username' type='text' value={nameValue} onChange={addNameHandler}></input>
                <label htmlFor='age'>Age (Years)</label>
                <input id='age' type='number' min='1' value={ageValue} onChange={addAgeHandler}></input>
                <Button type='submit'>Add user</Button>
            </form>
        </Card>
        </div>
    );

};


export default AddUser;
